package main

import "fmt"

func numOfSubarrays(arr []int, k, threshold int) int {
    ans, cnt := 0, 0

    for i, num := range arr {
        cnt += num
        if i &gt;= k {
            cnt -= arr[i-k]
        }

        if i &gt;= k-1 && cnt &gt;= k*threshold {
            ans += 1
        }
    }

    return ans
}

func main() {
    testCases := []struct {
        arr       []int
        k         int
        threshold int
