        {[]int{2, 2, 2, 2, 5, 5, 5, 8}, 3, 4},
        {[]int{11, 13, 17, 23, 29, 31, 7, 5, 2, 3}, 3, 5},
    }

    for _, testCase := range testCases {
        fmt.Println(numOfSubarrays(testCase.arr, testCase.k, testCase.threshold))
    }
}





