package main

import "fmt"

func maxSum(nums []int, m, k int) int64 {
    mp := make(map[int]int)
    var ans, cnt int64
    for i, num := range nums {
        cnt += int64(num)
        mp[num] += 1

        if i &gt;= k {
            cnt -= int64(nums[i-k])
            mp[nums[i-k]] -= 1
            if mp[nums[i-k]] == 0 {
                delete(mp, nums[i-k])
            }
        }

        if i &gt;= k-1 && len(mp) &gt;= m {
            ans = max(ans, cnt)
        }
    }

    return ans
}

func main() {
