        {[]int{5, 9, 9, 2, 4, 5, 4}, 1, 3},
        {[]int{1, 2, 1, 2, 1, 2, 1}, 3, 3},
    }

    for _, testCase := range testCases {
        fmt.Println(maxSum(testCase.nums, testCase.m, testCase.k))
    }
}





