)

func shortestBeautifulSubstring(s string, k int) string {
    if strings.Count(s, "1") &lt; k {
        return ""
    }
    ans := s
    cnt, left := 0, 0

    for i, ch := range s {
        if ch == '1' {
            cnt += 1
        }

        for cnt &gt;= k {
            tmp := s[left : i+1]
            if s[left] == '1' {
                cnt -= 1
            }
            left += 1
            if len(tmp) == len(ans) {
                if tmp &lt; ans {
                    ans = tmp
                }
            }
            if len(tmp) &lt; len(ans) {
                ans = tmp
            }
        }
    }

    return ans
}

