func minWindow(s string, t string) string {
    // å…ˆç»Ÿè®¡ä¸€ä¸‹tä¸­å„ä¸ªå­—ç¬¦çš„æ•°é‡,ç»Ÿè®¡ä¸€ä¸‹å­—ç¬¦çš„ç§ç±»
    mp := make(map[byte]int)

    for i := 0; i &lt; len(t); i++ {
        mp[t[i]] += 1
    }
    left := 0
    cnt := len(mp) // å­—ç¬¦çš„ç§ç±»
    ans := ""
    tmp := math.MaxInt

    for i := 0; i &lt; len(s); i++ {
        if _, ok := mp[s[i]]; ok { // å­—ç¬¦æœ‰æ•ˆ
            mp[s[i]] -= 1
            if mp[s[i]] == 0 { // ç¬¦åˆè¦æ±‚
